
public class PercentageCalculation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int m = 75;
		System.out.println("Marks scored in Maths: " + m);
		int s = 55;
		System.out.println("Marks scored in Science: " + s);
		int o = 80;
		System.out.println("Marks scored in Operating Systems: " + o);
		int d = 75;
		System.out.println("Marks scored in Dataware house: " + d);
		System.out.println("Total Marks Scored out of Maximum Marks= " + (m + s + o + d) + "/400");
		System.out.println("Percentage,(Marks scored/Maximum marks)*100= " + (m + s + o + d) * 0.25 + "%");

	}

}
